    def handler_goods(self, goods):
        if goods.get("status") == -1:
            return
        goods_type = goods.get("lemon_group_title")
        self.cursor.execute("select id from goods_type WHERE name='{0}' AND level={1}".format(goods_type.encode('utf-8'), 3))
